import AjaxOptions from '../interfaces/AjaxOptions';
import '../methods/trigger';
/**
 * 发送 ajax 请求
 * @param options
 * @example
```js
ajax({
  method: "POST",
  url: "some.php",
  data: { name: "John", location: "Boston" }
}).then(function( msg ) {
  alert( "Data Saved: " + msg );
});
```
 */
declare function ajax(options: AjaxOptions): Promise<any>;
export default ajax;
