import Selector from '../types/Selector';
import './find';
declare module '../JQ' {
    interface JQ<T = HTMLElement> {
        /**
         * 保留含有指定子元素的元素，去掉不含有指定子元素的元素
         * @param selector CSS 选择器或 DOM 元素
         * @example
    ```js
    // 给含有 ul 的 li 加上背景色
    $('li').has('ul').css('background-color', 'red');
    ```
         */
        has(selector: Selector | Element): this;
    }
}
